const max=document.querySelector("#max").value,increment=document.querySelector("#increment"),decrement=document.querySelector("#decrement"),quantity=document.querySelector(".quantity");let defaultVal=parseInt(quantity.value);increment.addEventListener("click",()=>{defaultVal>=max||(defaultVal++,quantity.value=defaultVal)}),decrement.addEventListener("click",()=>{defaultVal<=1||(defaultVal--,quantity.value=defaultVal)});const description_title=document.querySelector(".title-description"),additional_title=document.querySelector(".title-additional"),description_text=document.querySelector(".text-description"),additional_text=document.querySelector(".text-additional"),chev1=document.querySelector("#chev1"),chev2=document.querySelector("#chev2"),arrchevron=["bx bxs-chevron-down","bx bxs-chevron-up"];description_title.addEventListener("click",()=>{chev1.classList.value==arrchevron[0]?chev1.classList.value=arrchevron[1]:chev1.classList.value=arrchevron[0],description_text.classList.toggle("hidden")}),additional_title.addEventListener("click",()=>{chev2.classList.value==arrchevron[0]?chev2.classList.value=arrchevron[1]:chev2.classList.value=arrchevron[0],additional_text.classList.toggle("hidden")});